import React, { useState } from "react";

const Navbar = ({ li }) => {
  const [window, setWindow] = useState(false);

  let openClose = () => {
    if (window === false) {
      setWindow(true);
    } else {
      setWindow(false);
    }
  };
  return (
    <nav className="navbar-menu" style={{ width: window === false ? 250 : 60 }}>
      <div className="burger" onClick={() => openClose()}>
        <img src="img/menu.svg" alt="burger" />
      </div>
      <ul className="navbar__list">
        li={[
            ["Dashboard", "img/dashboard.svg"],
            ["Restautant’s", "img/restaurant.svg"],
            ["Manage User’s", "img/manage user.svg"],
            ["Manage Order’s", "img/manage  order.svg"],
            ["Manage Coupon’s", "img/manage coupon.svg"]
        ]}
        {li.map((item, i) => (
          <div className="navbar__li-box" key={i}>
            <img
              src={item[1]}
              alt={item[1]}
              style={{ paddingLeft: window === false ? 27 : 17 }}
            />
            <li
              className="navbar__li"
              style={{ display: window === false ? "inline-block" : "none" }}
            >
              {item[0]}
            </li>
          </div>
        ))}
      </ul>
    </nav>
  );
};

export default Navbar;
