import React, { useState } from 'react';
import styles from './Navbar.module.css'; // Import your CSS file for styling

const Navbar = ({ title, children }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSubMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <li className={ styles.menu-item } onMouseEnter={toggleSubMenu} onMouseLeave={toggleSubMenu}>
      {title}
      {isOpen && <ul className={ styles.submenu }>{children}</ul>}
    </li>
  );
};

const Menu = () => {
  return (
    <nav className={ styles.menu }>
      <ul>
        <Navbar title="Home" />
        <Navbar title="About">
          <Navbar title="Company" />
          <Navbar title="Team">
            <Navbar title="Management" />
            <Navbar title="Staff" />
          </Navbar>
          <Navbar title="History" />
        </Navbar>
        <Navbar title="Contact" />
      </ul>
    </nav>
  );
};

export default Navbar;
